import React, { useState } from "react";
import useInterval from "../utils/useInterval";
import BreakInterval from "./BreakInterval";
import FocusInterval from "./FocusInterval";
import PlayPause from "./PlayPause";
import Timer from "./Timer";

function Pomodoro() {
  // Timer starts out paused
  const [isTimerRunning, setIsTimerRunning] = useState(false);
  const [breakIntervalState, setBreakIntervalState] = useState(5)
  const [focusIntervalState, setFocusIntervalState] = useState(25)

  const increaseByFive = () => setFocusIntervalState((current) => current + 5) 
  const decreaseByFive = () => setFocusIntervalState((current) => current - 5) 
  const increaseByOne = () => {
    if (breakIntervalState === 1) {
      return;
    }
    setBreakIntervalState((current) => current + 1)
  } 
  const decreaseByOne = () => setBreakIntervalState((current) => current - 1) 

  useInterval(
    () => {
      // ToDo: Implement what should happen when the timer is running

    },
    isTimerRunning ? 1000 : null
  );

  function playPause() {
    setIsTimerRunning((prevState) => !prevState);
  }

  return (
    <div className="pomodoro">
      <div className="row">
        <FocusInterval focusInterval={focusIntervalState} upByFive={increaseByFive} downByFive={decreaseByFive} />
        <BreakInterval breakInterval={breakIntervalState} upByOne={increaseByOne} downByOne={decreaseByOne} />
      </div>
      <PlayPause playPause={playPause} isTimerRunning={isTimerRunning} />
      <Timer isTimerRunning={isTimerRunning} focusInterval={focusIntervalState} />
    </div>
  );
}

export default Pomodoro;
