import React from 'react';
import { minutesToDuration, secondsToDuration} from '../utils/duration';

function Timer(props) {
    const progressPercentage = (1-(props.timeRemainingState/(props.focusInterval*60)))*100
    const progressWidth = {
        width: progressPercentage + "%"
    }
    console.log(progressPercentage)

    return (
        <div style={props.isTimerRunning ? {display: "block"} : {display: "none"}}>
            {/* TODO: This area should show only when a focus or break session is running or pauses */}
            <div className="row mb-2">
                <div className="col">
                    {/* TODO: Update message below to include current session (Focusing or On Break) and total duration */}
                    <h2 data-testid="session-title">Focusing for {minutesToDuration(props.focusInterval)} minutes</h2>
                    {/* TODO: Update message below to include time remaining in the current session */}
                    <p className="lead" data-testid="session-sub-title">
                    {secondsToDuration(props.timeRemainingState)} remaining
                    </p>
                </div>
            </div>
            <div className="row mb-2">
                <div className="col">
                    <div className="progress" style={{ height: "20px" }}>
                    <div
                        className="progress-bar"
                        role="progressbar"
                        aria-valuemin="0"
                        aria-valuemax="100"
                        aria-valuenow={progressPercentage} // TODO: Increase aria-valuenow as elapsed time increases
                        style={progressWidth} // TODO: Increase width % as elapsed time increases
                    />
                    </div>
                </div>
            </div>
        </div>
    )
}

export default Timer;
